			;; This screen is displayed when the player has achieved a new level
			(eq screen "CommanderUpgrade")
				(list
					'screenName     (cat (objGetName gSource) "'s cargobay.")
					'hideBack       Nil
					'hideExit       Nil
					'initFunc	'(block Nil
						)
					'actions	(list
						(list
							)
						)
					)
			;; This screen browses personal combat equipment
			(eq screen "CommanderBrowse")
				(list
					'screenName     (cat (objGetName gSource) "'s cargobay.")
					'screenType     "ItemPickerPlayer"
					'itemFilter     "* +CommanderItem;"
					'hideBack       Nil
					'hideExit       Nil
					'initFunc	'(block Nil
						)
					'actions	(list
						(list
							)
						)
					)
